def create_animation():
    """
    Sets up and displays the point-light walker animation.
    """
    # Create a figure and axes for the animation
    fig, ax = plt.subplots(figsize=(5, 7))
    fig.set_facecolor('black')
    ax.set_facecolor('black')

    # Configure the plot aesthetics
    # Set axis limits to comfortably fit the walker's motion
    ax.set_xlim(-1, 1)
    ax.set_ylim(-1.2, 1.2)
    ax.set_aspect('equal', adjustable='box')
    
    # Hide the axes, ticks, and spines for a clean look
    ax.axis('off')

    # Initialize the scatter plot for the 15 points
    # Use the first frame of data for the initial positions of the points
    scatter = ax.scatter(
        WALKER_DATA[0, :, 0], 
        WALKER_DATA[0, :, 1], 
        color='white',
        s=70  # Adjust size of points if needed
    )

    def update(frame):
        """
        Update function for the animation, called for each frame.
        """
        # Get the (x, y) coordinates for the current frame
        coords = WALKER_DATA[frame, :, :]
        
        # Update the positions of the points in the scatter plot
        scatter.set_offsets(coords)
        
        # Return the updated artists. This is necessary for blitting.
        return scatter,

    # Determine the number of frames from the data array
    num_frames = WALKER_DATA.shape[0]

    # Create the animation object using FuncAnimation
    # interval: Delay between frames in milliseconds (e.g., 40ms -> 25fps)
    # blit=True: Optimizes animation by only redrawing changed parts
    ani = FuncAnimation(
        fig, 
        update, 
        frames=num_frames, 
        interval=40, 
        blit=True
    )

    # Display the animation window
    plt.show()

if __name__ == '__main__':
    create_animation()
